pragma solidity 0.8.25;

import {
    GelatoRelayContext
} from "@gelatonetwork/relay-context/contracts/GelatoRelayContext.sol";
import {
    IGelato1BalanceCCTPReceiver
} from "./interfaces/IGelato1BalanceCCTPReceiver.sol";
import {
    IGelato1BalanceCCTPSender
} from "./interfaces/IGelato1BalanceCCTPSender.sol";
import {ITokenMessenger} from "./interfaces/ITokenMessenger.sol";
import {IEIP3009Token} from "./interfaces/IEIP3009Token.sol";
import {Authorization} from "./types/Authorization.sol";
import {_computeVaultAddress} from "./Vault.sol";

contract Gelato1BalanceCCTPSender is
    IGelato1BalanceCCTPSender,
    GelatoRelayContext
{
    uint32 public immutable DST_DOMAIN;
    IEIP3009Token public immutable SRC_TOKEN;
    IEIP3009Token public immutable DST_TOKEN;
    ITokenMessenger public immutable TOKEN_MESSENGER;
    IGelato1BalanceCCTPReceiver public immutable GELATO_1BALANCE_RECEIVER;

    constructor(
        uint32 _dstDomain,
        IEIP3009Token _srcToken,
        IEIP3009Token _dstToken,
        ITokenMessenger _tokenMessenger,
        IGelato1BalanceCCTPReceiver _gelato1BalanceReceiver
    ) {
        DST_DOMAIN = _dstDomain;
        SRC_TOKEN = _srcToken;
        DST_TOKEN = _dstToken;
        TOKEN_MESSENGER = _tokenMessenger;
        GELATO_1BALANCE_RECEIVER = _gelato1BalanceReceiver;
    }

    function bridgeAndDeposit(
        address _owner,
        uint256 _amount,
        Authorization calldata _authorization
    ) external onlyGelatoRelay {
        SRC_TOKEN.receiveWithAuthorization(
            _owner,
            address(this),
            _amount,
            _authorization.validAfter,
            _authorization.validBefore,
            _authorization.nonce,
            _authorization.v,
            _authorization.r,
            _authorization.s
        );

        _transferRelayFee();

        uint256 remaining = _amount - _getFee();
        SRC_TOKEN.approve(address(TOKEN_MESSENGER), remaining);

        address sponsor = _decodeSponsor(_authorization);
        address vault = _computeVaultAddress(
            sponsor,
            DST_TOKEN,
            GELATO_1BALANCE_RECEIVER
        );

        TOKEN_MESSENGER.depositForBurnWithCaller(
            remaining,
            DST_DOMAIN,
            _addressToBytes32(vault),
            address(SRC_TOKEN),
            _addressToBytes32(address(GELATO_1BALANCE_RECEIVER))
        );

        emit LogBridge(sponsor, _amount);
    }

    function _decodeSponsor(
        Authorization calldata _authorization
    ) internal pure returns (address) {
        return address(uint160(uint256(_authorization.nonce)));
    }

    function _addressToBytes32(address addr) internal pure returns (bytes32) {
        return bytes32(uint256(uint160(addr)));
    }
}
","keccak256":"0x3b26949eda4a4ce2d88f4ee47c28314a731a4f4ee4add1129e30335a4de96dd7","license":"MIT"},"contracts/Vault.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.25;

import {IEIP3009Token} from "./interfaces/IEIP3009Token.sol";
import {
    IGelato1BalanceCCTPReceiver
} from "./interfaces/IGelato1BalanceCCTPReceiver.sol";

function _computeVaultAddress(
    address _owner,
    IEIP3009Token _token,
    IGelato1BalanceCCTPReceiver _gelato1BalanceReceiver
) pure returns (address) {
    bytes32 hashed = keccak256(
        abi.encodePacked(
            bytes1(0xff),
            _gelato1BalanceReceiver,
            keccak256(abi.encodePacked(_owner)),
            keccak256(
                abi.encodePacked(type(Vault).creationCode, abi.encode(_token))
            )
        )
    );
    return address(uint160(uint(hashed)));
}

contract Vault {
    constructor(IEIP3009Token _token) {
        _token.approve(msg.sender, type(uint256).max);
    }
}
